// Mock API service for frontend-only development
// This simulates backend responses without requiring a real server

const MOCK_DELAY = 500; // Simulate network delay

// Mock user data
let mockUser = {
  id: 1,
  name: 'GOWTHAMM',
  email: 'gowtham@example.com',
  phone: '+1 234 567 8900',
  location: 'New York, NY',
  bio: 'Fitness enthusiast and health advocate',
  age: 25,
  gender: 'male',
  height: 175,
  weight: 70,
  fitnessGoal: 'weight_loss',
  profilePhoto: null,
  settings: {
    theme: 'dark',
    language: 'en',
    units: 'metric'
  }
};

// Mock notifications
let mockNotifications = [
  {
    id: 1,
    type: 'achievement',
    title: 'Workout Complete!',
    message: 'Great job completing your morning workout',
    time: '2 hours ago',
    read: false,
    icon: 'ðŸ‹ï¸'
  },
  {
    id: 2,
    type: 'reminder',
    title: 'Water Intake Reminder',
    message: "Don't forget to drink water! You're 2 glasses behind today",
    time: '1 hour ago',
    read: false,
    icon: 'ðŸ’§'
  }
];

// Helper function to simulate API delay
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// Mock API responses
export const mockApi = {
  // User profile endpoints
  async getProfile() {
    await delay(MOCK_DELAY);
    return { data: mockUser };
  },

  async updateProfile(profileData) {
    await delay(MOCK_DELAY);
    mockUser = { ...mockUser, ...profileData };
    return { data: mockUser };
  },

  async updateSettings(settings) {
    await delay(MOCK_DELAY);
    mockUser.settings = { ...mockUser.settings, ...settings };
    return { data: mockUser.settings };
  },

  async updatePassword(passwordData) {
    await delay(MOCK_DELAY);
    // In a real app, this would validate the current password
    if (!passwordData.currentPassword) {
      throw new Error('Current password is required');
    }
    return { data: { message: 'Password updated successfully' } };
  },

  async deleteAccount() {
    await delay(MOCK_DELAY);
    return { data: { message: 'Account deleted successfully' } };
  },

  // Notifications endpoints
  async getNotifications() {
    await delay(MOCK_DELAY);
    return { data: mockNotifications };
  },

  async markNotificationAsRead(notificationId) {
    await delay(MOCK_DELAY);
    const notification = mockNotifications.find(n => n.id === notificationId);
    if (notification) {
      notification.read = true;
    }
    return { data: { message: 'Notification marked as read' } };
  },

  async markAllNotificationsAsRead() {
    await delay(MOCK_DELAY);
    mockNotifications.forEach(n => n.read = true);
    return { data: { message: 'All notifications marked as read' } };
  },

  // Progress tracking endpoints
  async getProgress() {
    await delay(MOCK_DELAY);
    return { data: [] };
  },

  async addProgressEntry(entry) {
    await delay(MOCK_DELAY);
    return { data: { ...entry, id: Date.now() } };
  },

  // Workout endpoints
  async getWorkouts() {
    await delay(MOCK_DELAY);
    return { data: [] };
  },

  async createWorkout(workout) {
    await delay(MOCK_DELAY);
    return { data: { ...workout, id: Date.now() } };
  },

  // Diet endpoints
  async getMeals() {
    await delay(MOCK_DELAY);
    return { data: [] };
  },

  async addMeal(meal) {
    await delay(MOCK_DELAY);
    return { data: { ...meal, id: Date.now() } };
  }
};

export default mockApi;
