import React, { useEffect, useState } from 'react';
import { Typography, Box, Card, CardContent, Grid, Button, Stack, TextField, Tabs, Tab, Alert } from '@mui/material';
import { People as CommunityIcon, EmojiEvents as ChallengesIcon, Leaderboard as LeaderboardIcon, Forum as ForumIcon } from '@mui/icons-material';
import HeroHeader from '../components/layout/HeroHeader';
import { useAuth } from '../context/AuthContext';
import { communityService } from '../services/communityService';

const CommunityPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [challenges, setChallenges] = useState([]);
  const [leaderboard, setLeaderboard] = useState([]);
  const [threads, setThreads] = useState([]);
  const [friends, setFriends] = useState([]);
  const [newThreadTitle, setNewThreadTitle] = useState('');
  const [commentDrafts, setCommentDrafts] = useState({});
  const { isAuthenticated } = useAuth();
  const [newChallenge, setNewChallenge] = useState({ title: '', description: '' });

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      setError(null);
      try {
        const [challengesRes, leaderboardRes, forumsRes, friendsRes] = await Promise.all([
          communityService.listChallenges().catch(() => null),
          communityService.leaderboard().catch(() => null),
          communityService.listForums().catch(() => null),
          communityService.listFriends().catch(() => null)
        ]);

        // Normalize results; backend currently returns placeholder messages
        const normalizedChallenges = Array.isArray(challengesRes?.items)
          ? challengesRes.items
          : [];
        const normalizedLeaderboard = Array.isArray(leaderboardRes?.items)
          ? leaderboardRes.items
          : [];

        setChallenges(normalizedChallenges);
        setLeaderboard(normalizedLeaderboard);
        setThreads(Array.isArray(forumsRes?.items) ? forumsRes.items : []);
        setFriends(Array.isArray(friendsRes?.items) ? friendsRes.items : []);
      } catch (e) {
        setError('Failed to load community data');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const handleJoin = async (id) => {
    try {
      await communityService.joinChallenge(id);
      // Optional: refresh leaderboard/challenges after join
    } catch (e) {
      // Silently ignore for now; can surface a toast/snackbar later
    }
  };

  const handleAddThread = async () => {
    if (!newThreadTitle.trim()) return;
    try {
      const created = await communityService.createThread(newThreadTitle.trim());
      setThreads((prev) => [created, ...prev]);
      setNewThreadTitle('');
    } catch {}
  };

  const handleAddComment = async (challengeId) => {
    const text = (commentDrafts[challengeId] || '').trim();
    if (!text) return;
    try {
      const created = await communityService.addComment(challengeId, text);
      setChallenges((prev) => prev.map(c => c.id === challengeId ? { ...c, comments: [created, ...(c.comments || [])] } : c));
      setCommentDrafts((d) => ({ ...d, [challengeId]: '' }));
    } catch {}
  };

  const [activeTab, setActiveTab] = useState(0);

  const handleTabChange = (e, v) => setActiveTab(v);

  const handleCreateChallenge = async () => {
    const title = newChallenge.title.trim();
    if (!title) return;
    try {
      const created = await communityService.createChallenge({ title, description: newChallenge.description.trim() });
      setChallenges((prev) => [created, ...prev]);
      setNewChallenge({ title: '', description: '' });
    } catch {}
  };

  const refreshLeaderboard = async () => {
    try {
      const res = await communityService.leaderboard();
      setLeaderboard(Array.isArray(res?.items) ? res.items : []);
    } catch {}
  };

  const handleQuickPoints = async () => {
    try {
      const challenge = await communityService.createChallenge({ title: 'Quick Points Boost', description: 'Auto-created test challenge' });
      await communityService.joinChallenge(challenge._id || challenge.id);
      await refreshLeaderboard();
    } catch {}
  };

  return (
    <Box>
      <HeroHeader
        icon={<CommunityIcon fontSize="large" />}
        title="Community & Challenges"
        subtitle="Join fitness challenges, connect with others, and stay motivated together"
      />

      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs value={activeTab} onChange={handleTabChange} aria-label="community tabs" textColor="primary" indicatorColor="primary">
          <Tab icon={<ChallengesIcon />} iconPosition="start" label="Challenges" />
          <Tab icon={<LeaderboardIcon />} iconPosition="start" label="Leaderboard" />
          <Tab icon={<ForumIcon />} iconPosition="start" label="Forum" />
        </Tabs>
      </Box>

      {activeTab === 0 && (
        <Grid container spacing={3} sx={{ mt: 1 }}>
          <Grid item xs={12}>
          <Card className="card">
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Challenges
              </Typography>
              {isAuthenticated && (
                <Box mb={2}>
                  <Typography variant="subtitle2" sx={{ mb: 1 }}>Create a new challenge</Typography>
                  <Box display="flex" gap={1} flexWrap="wrap">
                    <TextField
                      size="small"
                      placeholder="Title"
                      value={newChallenge.title}
                      onChange={(e) => setNewChallenge((c) => ({ ...c, title: e.target.value }))}
                      sx={{ minWidth: 240 }}
                    />
                    <TextField
                      size="small"
                      placeholder="Description (optional)"
                      value={newChallenge.description}
                      onChange={(e) => setNewChallenge((c) => ({ ...c, description: e.target.value }))}
                      sx={{ flex: 1, minWidth: 260 }}
                    />
                    <Button variant="contained" onClick={handleCreateChallenge}>Add</Button>
                  </Box>
                </Box>
              )}
              {loading ? (
                <Typography variant="body2" color="text.secondary">Loading…</Typography>
              ) : error ? (
                <Typography variant="body2" color="error">{error}</Typography>
              ) : challenges.length === 0 ? (
                <Typography variant="body2" color="text.secondary">
                  No challenges yet. Stay tuned!
                </Typography>
              ) : (
                <Stack spacing={2}>
                  {challenges.map((c) => (
                    <Box key={c.id}>
                      <Box display="flex" alignItems="center" justifyContent="space-between">
                        <Box>
                          <Typography variant="subtitle1">{c.title}</Typography>
                          <Typography variant="caption" color="text.secondary">{c.description}</Typography>
                        </Box>
                        <Button size="small" variant="contained" onClick={() => handleJoin(c.id)}>Join</Button>
                      </Box>
                      <Box mt={1}>
                        <Typography variant="caption" color="text.secondary">Comments</Typography>
                        <Stack spacing={1} mt={0.5}>
                          {(c.comments || []).slice(0, 3).map(cm => (
                            <Typography key={cm.id} variant="body2">{cm.userName || 'User'}: {cm.text}</Typography>
                          ))}
                          <Box display="flex" gap={1}>
                            <TextField
                              size="small"
                              placeholder="Add a comment"
                              fullWidth
                              value={commentDrafts[c.id] || ''}
                              onChange={(e) => setCommentDrafts((d) => ({ ...d, [c.id]: e.target.value }))}
                            />
                            <Button size="small" variant="outlined" onClick={() => handleAddComment(c.id)}>Post</Button>
                          </Box>
                        </Stack>
                      </Box>
                    </Box>
                  ))}
                </Stack>
              )}
            </CardContent>
          </Card>
          </Grid>
        </Grid>
      )}

      {activeTab === 1 && (
        <Grid container spacing={3} sx={{ mt: 1 }}>
          <Grid item xs={12}>
          <Card className="card">
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Leaderboard
              </Typography>
              {isAuthenticated && (
                <Box mb={2}>
                  <Button variant="outlined" onClick={handleQuickPoints}>Earn 10 pts (test)</Button>
                </Box>
              )}
              {loading ? (
                <Typography variant="body2" color="text.secondary">Loading…</Typography>
              ) : error ? (
                <Typography variant="body2" color="error">{error}</Typography>
              ) : leaderboard.length === 0 ? (
                <Typography variant="body2" color="text.secondary">
                  No leaderboard data yet.
                </Typography>
              ) : (
                <Stack spacing={1}>
                  {leaderboard.map((u, idx) => (
                    <Box key={u.id} display="flex" alignItems="center" justifyContent="space-between">
                      <Typography variant="body2">#{idx + 1} {u.name}</Typography>
                      <Typography variant="body2" color="text.secondary">{u.points} pts</Typography>
                    </Box>
                  ))}
                </Stack>
              )}
            </CardContent>
          </Card>
          </Grid>
        </Grid>
      )}

      {activeTab === 2 && (
        <Grid container spacing={3} sx={{ mt: 1 }}>
          <Grid item xs={12}>
          <Card className="card">
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Community Forum
              </Typography>
              <Box display="flex" gap={1} mb={2}>
                <TextField
                  size="small"
                  placeholder="Start a new topic"
                  fullWidth
                  value={newThreadTitle}
                  onChange={(e) => setNewThreadTitle(e.target.value)}
                />
                <Button variant="contained" onClick={handleAddThread}>Post</Button>
              </Box>
              {threads.length === 0 ? (
                <Typography variant="body2" color="text.secondary">No discussions yet.</Typography>
              ) : (
                <Stack spacing={1}>
                  {threads.map(t => (
                    <Box key={t.id}>
                      <Typography variant="subtitle2">{t.title}</Typography>
                      <Typography variant="caption" color="text.secondary">by {t.author}</Typography>
                    </Box>
                  ))}
                </Stack>
              )}
            </CardContent>
          </Card>
          </Grid>
        </Grid>
      )}
    </Box>
  );
};

export default CommunityPage;
