import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Grid,
  Alert,
  Chip,
  LinearProgress
} from '@mui/material';
import {
  Add as AddIcon,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  CheckCircle as CheckCircleIcon
} from '@mui/icons-material';

const WeightProgressTracker = ({ onWeightLogged }) => {
  const [weightEntries, setWeightEntries] = useState([]);
  const [newWeight, setNewWeight] = useState('');
  const [newDate, setNewDate] = useState(new Date().toISOString().split('T')[0]);
  const [notes, setNotes] = useState('');

  useEffect(() => {
    loadWeightEntries();
  }, []);

  const loadWeightEntries = () => {
    const savedEntries = JSON.parse(localStorage.getItem('weightEntries') || '[]');
    setWeightEntries(savedEntries.sort((a, b) => new Date(b.date) - new Date(a.date)));
  };

  const addWeightEntry = () => {
    if (!newWeight || !newDate) return;

    const weight = parseFloat(newWeight);
    const entry = {
      id: Date.now(),
      weight: weight,
      date: newDate,
      notes: notes,
      timestamp: new Date().toISOString()
    };

    const updatedEntries = [entry, ...weightEntries];
    setWeightEntries(updatedEntries);
    localStorage.setItem('weightEntries', JSON.stringify(updatedEntries));

    // Reset form
    setNewWeight('');
    setNotes('');

    // Notify parent component
    if (onWeightLogged) {
      onWeightLogged(entry);
    }
  };

  const getProgressStats = () => {
    if (weightEntries.length < 2) return null;

    const sortedEntries = [...weightEntries].sort((a, b) => new Date(a.date) - new Date(b.date));
    const firstWeight = sortedEntries[0].weight;
    const currentWeight = sortedEntries[sortedEntries.length - 1].weight;
    const targetWeight = 70; // This should come from user's dashboard data

    const totalChange = currentWeight - firstWeight;
    const changeFromTarget = currentWeight - targetWeight;
    const progressToTarget = ((firstWeight - currentWeight) / (firstWeight - targetWeight)) * 100;

    return {
      firstWeight,
      currentWeight,
      targetWeight,
      totalChange,
      changeFromTarget,
      progressToTarget: Math.max(0, Math.min(100, progressToTarget)),
      entriesCount: weightEntries.length,
      daysTracked: Math.ceil((new Date(sortedEntries[sortedEntries.length - 1].date) - new Date(sortedEntries[0].date)) / (1000 * 60 * 60 * 24))
    };
  };

  const getWeightTrend = () => {
    if (weightEntries.length < 2) return 'stable';

    const recentEntries = weightEntries.slice(0, 3);
    const weights = recentEntries.map(entry => entry.weight);

    let increasing = 0;
    let decreasing = 0;

    for (let i = 1; i < weights.length; i++) {
      if (weights[i] > weights[i-1]) increasing++;
      else if (weights[i] < weights[i-1]) decreasing++;
    }

    if (increasing > decreasing) return 'increasing';
    if (decreasing > increasing) return 'decreasing';
    return 'stable';
  };

  const getTrendIcon = (trend) => {
    switch (trend) {
      case 'increasing': return <TrendingUpIcon color="warning" />;
      case 'decreasing': return <TrendingDownIcon color="success" />;
      default: return <CheckCircleIcon color="info" />;
    }
  };

  const getTrendColor = (trend) => {
    switch (trend) {
      case 'increasing': return 'warning';
      case 'decreasing': return 'success';
      default: return 'info';
    }
  };

  const getTrendLabel = (trend) => {
    switch (trend) {
      case 'increasing': return 'Weight Increasing';
      case 'decreasing': return 'Weight Decreasing';
      default: return 'Weight Stable';
    }
  };

  const stats = getProgressStats();
  const trend = getWeightTrend();

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          Weight Progress Tracker
        </Typography>

        <Grid container spacing={3} sx={{ mb: 3 }}>
          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              label="Weight (kg)"
              type="number"
              value={newWeight}
              onChange={(e) => setNewWeight(e.target.value)}
              inputProps={{ min: 0, step: 0.1 }}
              placeholder="e.g., 75.5"
            />
          </Grid>

          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              label="Date"
              type="date"
              value={newDate}
              onChange={(e) => setNewDate(e.target.value)}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>

          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              label="Notes (optional)"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="e.g., After workout, morning weight"
            />
          </Grid>
        </Grid>

        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={addWeightEntry}
          disabled={!newWeight || !newDate}
          fullWidth
          sx={{ mb: 3 }}
        >
          Log Weight Entry
        </Button>

        {stats && (
          <Box sx={{ mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Progress Summary
            </Typography>

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={12} sm={6} md={3}>
                <Box textAlign="center">
                  <Typography variant="h4" color="primary">
                    {stats.currentWeight}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Current Weight (kg)
                  </Typography>
                </Box>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Box textAlign="center">
                  <Typography variant="h4" color="secondary">
                    {stats.targetWeight}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Target Weight (kg)
                  </Typography>
                </Box>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Box textAlign="center">
                  <Typography variant="h4" color={stats.totalChange > 0 ? 'warning' : 'success'}>
                    {stats.totalChange > 0 ? '+' : ''}{stats.totalChange.toFixed(1)}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Change (kg)
                  </Typography>
                </Box>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Box textAlign="center">
                  <Typography variant="h4" color="info">
                    {stats.entriesCount}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Entries Logged
                  </Typography>
                </Box>
              </Grid>
            </Grid>

            <Box sx={{ mb: 2 }}>
              <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                <Typography variant="body2">
                  Progress to Target Weight
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {stats.progressToTarget.toFixed(1)}%
                </Typography>
              </Box>
              <LinearProgress 
                variant="determinate" 
                value={stats.progressToTarget} 
                sx={{ height: 10, borderRadius: 5 }}
                color={stats.progressToTarget >= 100 ? 'success' : 'primary'}
              />
            </Box>

            <Box display="flex" gap={1} alignItems="center" mb={2}>
              {getTrendIcon(trend)}
              <Chip 
                label={getTrendLabel(trend)} 
                color={getTrendColor(trend)}
                size="small"
              />
              <Typography variant="body2" color="text.secondary">
                â€¢ {stats.daysTracked} days tracked
              </Typography>
            </Box>
          </Box>
        )}

        {weightEntries.length > 0 && (
          <Box>
            <Typography variant="h6" gutterBottom>
              Recent Entries
            </Typography>

            <Grid container spacing={2}>
              {weightEntries.slice(0, 5).map((entry) => (
                <Grid item xs={12} sm={6} md={4} key={entry.id}>
                  <Card variant="outlined">
                    <CardContent sx={{ textAlign: 'center', py: 2 }}>
                      <Typography variant="h5" color="primary" gutterBottom>
                        {entry.weight} kg
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {new Date(entry.date).toLocaleDateString()}
                      </Typography>
                      {entry.notes && (
                        <Typography variant="caption" display="block" sx={{ mt: 1 }}>
                          "{entry.notes}"
                        </Typography>
                      )}
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}

        {weightEntries.length === 0 && (
          <Alert severity="info">
            <Typography variant="body2">
              <strong>Start tracking your weight!</strong> Log your first weight entry to begin monitoring your progress.
            </Typography>
          </Alert>
        )}
      </CardContent>
    </Card>
  );
};

export default WeightProgressTracker;
