import React, { useEffect, useState } from 'react';
import { 
  Typography, 
  Box, 
  Card, 
  CardContent, 
  Grid, 
  Avatar, 
  TextField, 
  Button, 
  Stack,
  Tabs,
  Tab,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Switch,
  FormControlLabel,
  Divider,
  Chip,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  ListItemSecondaryAction,
  Paper,
  Stepper,
  Step,
  StepLabel,
  Fade,
  Zoom
} from '@mui/material';
import {
  Person as ProfileIcon,
  Settings as SettingsIcon,
  Security as SecurityIcon,
  Notifications as NotificationsIcon,
  Palette as ThemeIcon,
  Language as LanguageIcon,
  Delete as DeleteIcon,
  Edit as EditIcon,
  Save as SaveIcon,
  Cancel as CancelIcon,
  CameraAlt as CameraIcon,
  Email as EmailIcon,
  Phone as PhoneIcon,
  LocationOn as LocationIcon,
  Cake as AgeIcon,
  Height as HeightIcon,
  MonitorWeight as WeightIcon,
  Wc as GenderIcon,
  Flag as GoalIcon,
  Visibility as VisibilityIcon,
  VisibilityOff as VisibilityOffIcon,
  CheckCircle as CheckIcon,
  Warning as WarningIcon
} from '@mui/icons-material';
import HeroHeader from '../components/layout/HeroHeader';
import { useAuth } from '../context/AuthContext';
import { usersService } from '../services/usersService';

const ProfilePage = () => {
  const { user, updateUser } = useAuth();
  const [activeTab, setActiveTab] = useState(0);
  const [form, setForm] = useState({ 
    name: '', 
    email: '', 
    age: '', 
    gender: '', 
    height: '', 
    weight: '', 
    fitnessGoal: '',
    phone: '',
    location: '',
    bio: ''
  });
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [settings, setSettings] = useState({
    emailNotifications: true,
    workoutReminders: true,
    goalReminders: true,
    weeklyReports: true,
    theme: 'light',
    language: 'en',
    units: 'metric'
  });
  const [saving, setSaving] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [showPassword, setShowPassword] = useState({ current: false, new: false, confirm: false });
  const [deleteDialog, setDeleteDialog] = useState(false);
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    const loadProfile = async () => {
      try {
        const data = await usersService.getProfile();
        setForm({
          name: data.name || '',
          email: data.email || '',
          age: data.age || '',
          gender: data.gender || '',
          height: data.height || '',
          weight: data.weight || '',
          fitnessGoal: data.fitnessGoal || '',
          phone: data.phone || '',
          location: data.location || '',
          bio: data.bio || ''
        });
        updateUser(data);
      } catch (e) {
        setError('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };
    loadProfile();
  }, [updateUser]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordForm((f) => ({ ...f, [name]: value }));
  };

  const handleSettingsChange = (e) => {
    const { name, value, checked } = e.target;
    setSettings((s) => ({ 
      ...s, 
      [name]: e.target.type === 'checkbox' ? checked : value 
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    setError(null);
    setSuccess(null);
    try {
      const updated = await usersService.updateProfile({
        ...form,
        age: form.age ? Number(form.age) : undefined,
        height: form.height ? Number(form.height) : undefined,
        weight: form.weight ? Number(form.weight) : undefined
      });
      updateUser(updated);
      setSuccess('Profile updated successfully!');
      setEditing(false);
    } catch (e) {
      setError(e.message || 'Failed to save');
    } finally {
      setSaving(false);
    }
  };

  const handlePasswordUpdate = async () => {
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      setError('New passwords do not match');
      return;
    }
    if (passwordForm.newPassword.length < 6) {
      setError('New password must be at least 6 characters');
      return;
    }

    setSaving(true);
    setError(null);
    try {
      await usersService.updatePassword({
        currentPassword: passwordForm.currentPassword,
        newPassword: passwordForm.newPassword
      });
      setSuccess('Password updated successfully!');
      setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
    } catch (e) {
      setError(e.message || 'Failed to update password');
    } finally {
      setSaving(false);
    }
  };

  const handleSettingsSave = async () => {
    setSaving(true);
    setError(null);
    try {
      await usersService.updateSettings(settings);
      setSuccess('Settings updated successfully!');
    } catch (e) {
      setError(e.message || 'Failed to save settings');
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteAccount = async () => {
    setSaving(true);
    try {
      await usersService.deleteAccount();
      // This would typically redirect to login or show a confirmation
      setSuccess('Account deleted successfully');
    } catch (e) {
      setError(e.message || 'Failed to delete account');
    } finally {
      setSaving(false);
      setDeleteDialog(false);
    }
  };

  const togglePasswordVisibility = (field) => {
    setShowPassword(prev => ({ ...prev, [field]: !prev[field] }));
  };

  const getBMI = () => {
    if (!form.height || !form.weight) return null;
    const heightInMeters = form.height / 100;
    return (form.weight / (heightInMeters * heightInMeters)).toFixed(1);
  };

  const getBMICategory = (bmi) => {
    if (bmi < 18.5) return { category: 'Underweight', color: 'warning' };
    if (bmi < 25) return { category: 'Normal', color: 'success' };
    if (bmi < 30) return { category: 'Overweight', color: 'warning' };
    return { category: 'Obese', color: 'error' };
  };

  return (
    <Box>
      <HeroHeader
        icon={<ProfileIcon fontSize="large" />}
        title="Profile & Settings"
        subtitle="Manage your profile, update personal information, and customize your fitness goals"
      />

      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12} md={4}>
          <Card className="card">
            <CardContent sx={{ textAlign: 'center' }}>
              <Avatar
                sx={{ width: 100, height: 100, mx: 'auto', mb: 2 }}
                alt={user?.name || 'User'}
              >
                {user?.name?.charAt(0) || 'U'}
              </Avatar>
              <Typography variant="h6" gutterBottom>
                {user?.name || 'User Name'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {user?.email || 'user@example.com'}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={8}>
          <Card className="card">
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Profile
              </Typography>
              {loading ? (
                <Typography variant="body2" color="text.secondary">Loading…</Typography>
              ) : (
                <Stack spacing={2}>
                  {error && <Typography variant="body2" color="error">{error}</Typography>}
                  <TextField label="Name" name="name" value={form.name} onChange={handleChange} fullWidth />
                  <TextField label="Email" name="email" value={form.email} onChange={handleChange} fullWidth />
                  <Box display="flex" gap={2}>
                    <TextField label="Age" name="age" type="number" value={form.age} onChange={handleChange} fullWidth />
                    <TextField label="Gender" name="gender" value={form.gender} onChange={handleChange} fullWidth />
                  </Box>
                  <Box display="flex" gap={2}>
                    <TextField label="Height (cm)" name="height" type="number" value={form.height} onChange={handleChange} fullWidth />
                    <TextField label="Weight (kg)" name="weight" type="number" value={form.weight} onChange={handleChange} fullWidth />
                  </Box>
                  <TextField label="Fitness Goal" name="fitnessGoal" value={form.fitnessGoal} onChange={handleChange} fullWidth multiline minRows={2} />
                  <Box>
                    <Button variant="contained" onClick={handleSave} disabled={saving}>{saving ? 'Saving…' : 'Save Changes'}</Button>
                  </Box>
                </Stack>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ProfilePage;
