import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Grid,
  Alert,
  Chip,
  LinearProgress,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  CheckCircle as CheckCircleIcon,
  TrendingUp as TrendingUpIcon,
  Flag as GoalIcon
} from '@mui/icons-material';

const GoalTracker = () => {
  const [goals, setGoals] = useState([]);
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [editingGoal, setEditingGoal] = useState(null);
  const [goalForm, setGoalForm] = useState({
    title: '',
    type: 'weight',
    target: '',
    current: '',
    deadline: '',
    description: ''
  });

  useEffect(() => {
    loadGoals();
  }, []);

  const loadGoals = () => {
    const savedGoals = JSON.parse(localStorage.getItem('fitnessGoals') || '[]');
    setGoals(savedGoals);
  };

  const handleGoalChange = (e) => {
    const { name, value } = e.target;
    setGoalForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const addGoal = () => {
    if (!goalForm.title || !goalForm.target || !goalForm.deadline) return;

    const goal = {
      ...goalForm,
      id: Date.now(),
      createdAt: new Date().toISOString(),
      completed: false,
      progress: 0
    };

    const updatedGoals = [...goals, goal];
    setGoals(updatedGoals);
    localStorage.setItem('fitnessGoals', JSON.stringify(updatedGoals));

    // Reset form
    setGoalForm({
      title: '',
      type: 'weight',
      target: '',
      current: '',
      deadline: '',
      description: ''
    });
    setShowAddDialog(false);
  };

  const updateGoal = () => {
    if (!editingGoal || !goalForm.title || !goalForm.target || !goalForm.deadline) return;

    const updatedGoals = goals.map(goal => 
      goal.id === editingGoal.id 
        ? { ...goal, ...goalForm, target: parseFloat(goalForm.target), current: parseFloat(goalForm.current) || 0 }
        : goal
    );

    setGoals(updatedGoals);
    localStorage.setItem('fitnessGoals', JSON.stringify(updatedGoals));

    setEditingGoal(null);
    setGoalForm({
      title: '',
      type: 'weight',
      target: '',
      current: '',
      deadline: '',
      description: ''
    });
  };

  const deleteGoal = (goalId) => {
    const updatedGoals = goals.filter(goal => goal.id !== goalId);
    setGoals(updatedGoals);
    localStorage.setItem('fitnessGoals', JSON.stringify(updatedGoals));
  };

  const editGoal = (goal) => {
    setEditingGoal(goal);
    setGoalForm({
      title: goal.title,
      type: goal.type,
      target: goal.target.toString(),
      current: goal.current ? goal.current.toString() : '',
      deadline: goal.deadline,
      description: goal.description || ''
    });
  };

  const calculateProgress = (goal) => {
    if (goal.type === 'weight') {
      const current = goal.current || 0;
      const target = goal.target;
      const start = goal.startValue || goal.current || 0;

      if (target < start) {
        // Weight loss goal
        const totalToLose = start - target;
        const lost = start - current;
        return Math.min(100, Math.max(0, (lost / totalToLose) * 100));
      } else {
        // Weight gain goal
        const totalToGain = target - start;
        const gained = current - start;
        return Math.min(100, Math.max(0, (gained / totalToGain) * 100));
      }
    } else if (goal.type === 'workout') {
      const current = goal.current || 0;
      const target = goal.target;
      return Math.min(100, Math.max(0, (current / target) * 100));
    } else if (goal.type === 'streak') {
      const current = goal.current || 0;
      const target = goal.target;
      return Math.min(100, Math.max(0, (current / target) * 100));
    }
    return 0;
  };

  const getGoalStatus = (goal) => {
    const progress = calculateProgress(goal);
    const daysLeft = Math.ceil((new Date(goal.deadline) - new Date()) / (1000 * 60 * 60 * 24));

    if (progress >= 100) return { status: 'Completed', color: 'success', icon: <CheckCircleIcon /> };
    if (daysLeft < 0) return { status: 'Overdue', color: 'error', icon: <TrendingUpIcon /> };
    if (daysLeft <= 7) return { status: 'Due Soon', color: 'warning', icon: <TrendingUpIcon /> };
    return { status: 'In Progress', color: 'info', icon: <TrendingUpIcon /> };
  };

  const getGoalIcon = (type) => {
    switch (type) {
      case 'weight': return 'âš–ï¸';
      case 'workout': return 'ðŸ‹ï¸';
      case 'streak': return 'ðŸ”¥';
      case 'nutrition': return 'ðŸŽ';
      default: return 'ðŸŽ¯';
    }
  };

  const getGoalTypeLabel = (type) => {
    switch (type) {
      case 'weight': return 'Weight Goal';
      case 'workout': return 'Workout Goal';
      case 'streak': return 'Streak Goal';
      case 'nutrition': return 'Nutrition Goal';
      default: return 'Custom Goal';
    }
  };

  const getGoalUnit = (type) => {
    switch (type) {
      case 'weight': return 'kg';
      case 'workout': return 'workouts';
      case 'streak': return 'days';
      case 'nutrition': return 'calories';
      default: return '';
    }
  };

  const openAddDialog = () => {
    setGoalForm({
      title: '',
      type: 'weight',
      target: '',
      current: '',
      deadline: '',
      description: ''
    });
    setShowAddDialog(true);
  };

  const closeDialog = () => {
    setShowAddDialog(false);
    setEditingGoal(null);
    setGoalForm({
      title: '',
      type: 'weight',
      target: '',
      current: '',
      deadline: '',
      description: ''
    });
  };

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h6">
          Goal Tracker
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={openAddDialog}
        >
          Add New Goal
        </Button>
      </Box>

      {goals.length === 0 ? (
        <Card>
          <CardContent sx={{ textAlign: 'center', py: 4 }}>
            <GoalIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
            <Typography variant="h6" color="text.secondary" gutterBottom>
              No goals set yet
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Set your first fitness goal to start tracking your progress!
            </Typography>
          </CardContent>
        </Card>
      ) : (
        <Grid container spacing={3}>
          {goals.map((goal) => {
            const progress = calculateProgress(goal);
            const status = getGoalStatus(goal);
            const daysLeft = Math.ceil((new Date(goal.deadline) - new Date()) / (1000 * 60 * 60 * 24));

            return (
              <Grid item xs={12} md={6} key={goal.id}>
                <Card>
                  <CardContent>
                    <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                      <Box display="flex" alignItems="center" gap={1}>
                        <span>{getGoalIcon(goal.type)}</span>
                        <Typography variant="h6">
                          {goal.title}
                        </Typography>
                      </Box>
                      <Box display="flex" gap={1}>
                        <IconButton
                          size="small"
                          onClick={() => editGoal(goal)}
                          color="primary"
                        >
                          <EditIcon />
                        </IconButton>
                        <IconButton
                          size="small"
                          onClick={() => deleteGoal(goal.id)}
                          color="error"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Box>
                    </Box>

                    <Chip 
                      label={getGoalTypeLabel(goal.type)} 
                      size="small" 
                      variant="outlined"
                      sx={{ mb: 1 }}
                    />
                    <Chip 
                      label={status.status} 
                      color={status.color}
                      size="small"
                      icon={status.icon}
                      sx={{ ml: 1 }}
                    />

                    <Typography variant="body2" color="text.secondary" sx={{ mt: 2, mb: 1 }}>
                      {goal.description}
                    </Typography>

                    <Box sx={{ mb: 2 }}>
                      <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                        <Typography variant="body2">
                          Progress: {progress.toFixed(1)}%
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {goal.current || 0} / {goal.target} {getGoalUnit(goal.type)}
                        </Typography>
                      </Box>
                      <LinearProgress 
                        variant="determinate" 
                        value={progress} 
                        color={progress >= 100 ? 'success' : 'primary'}
                        sx={{ height: 8, borderRadius: 4 }}
                      />
                    </Box>

                    <Box display="flex" justifyContent="space-between" alignItems="center">
                      <Typography variant="caption" color="text.secondary">
                        Deadline: {new Date(goal.deadline).toLocaleDateString()}
                      </Typography>
                      <Typography variant="caption" color={daysLeft < 0 ? 'error.main' : daysLeft <= 7 ? 'warning.main' : 'text.secondary'}>
                        {daysLeft < 0 ? `${Math.abs(daysLeft)} days overdue` : 
                         daysLeft === 0 ? 'Due today' : 
                         daysLeft === 1 ? 'Due tomorrow' : 
                         `${daysLeft} days left`}
                      </Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            );
          })}
        </Grid>
      )}

      {/* Add/Edit Goal Dialog */}
      <Dialog
        open={showAddDialog || !!editingGoal}
        onClose={closeDialog}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {editingGoal ? 'Edit Goal' : 'Add New Goal'}
        </DialogTitle>

        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Goal Title"
                name="title"
                value={goalForm.title}
                onChange={handleGoalChange}
                placeholder="e.g., Lose 5kg, Complete 30 workouts this month"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Goal Type</InputLabel>
                <Select
                  name="type"
                  value={goalForm.type}
                  onChange={handleGoalChange}
                  label="Goal Type"
                >
                  <MenuItem value="weight">Weight Goal</MenuItem>
                  <MenuItem value="workout">Workout Goal</MenuItem>
                  <MenuItem value="streak">Streak Goal</MenuItem>
                  <MenuItem value="nutrition">Nutrition Goal</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Target Value"
                name="target"
                type="number"
                value={goalForm.target}
                onChange={handleGoalChange}
                placeholder="e.g., 70"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Current Value (optional)"
                name="current"
                type="number"
                value={goalForm.current}
                onChange={handleGoalChange}
                placeholder="e.g., 75"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Deadline"
                name="deadline"
                type="date"
                value={goalForm.deadline}
                onChange={handleGoalChange}
                InputLabelProps={{ shrink: true }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Description (optional)"
                name="description"
                value={goalForm.description}
                onChange={handleGoalChange}
                multiline
                rows={3}
                placeholder="Describe your goal and motivation..."
              />
            </Grid>
          </Grid>
        </DialogContent>

        <DialogActions>
          <Button onClick={closeDialog}>
            Cancel
          </Button>
          <Button 
            onClick={editingGoal ? updateGoal : addGoal}
            variant="contained"
            disabled={!goalForm.title || !goalForm.target || !goalForm.deadline}
          >
            {editingGoal ? 'Update Goal' : 'Add Goal'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default GoalTracker;
